var ticker_currencies="USD,EUR,JPY,CNY";var update_interval=30000;function ticker(a){var b={USD:"$",CNY:"¥",JPY:"¥",EUR:"€"};$.ajax({type:"GET",url:"https://min-api.cryptocompare.com/data/price?fsym=BCH&tsyms="+a,contentType:"application/json; charset=utf-8",timeout:6000,error:function(d,e,c){if(!$("#ticker_value").html().includes("BCH")){$("#ticker_value").html("N/A")}},success:function(d){var c=[];$.each(d,function(e,g){var f=b[e];if(f===undefined){return}c.push("BCH/"+e+"&nbsp;"+f+g)});if(c.length>0){$("#ticker_value").html(c.join(" &bull; "));return}if(!$("#ticker_value").html().includes("BCH")){$("#ticker_value").html("N/A")}}}).done(function(){setTimeout(function(){ticker(ticker_currencies)},update_interval)}).fail(function(){setTimeout(function(){ticker(ticker_currencies)},update_interval)})}ticker(ticker_currencies);